mapper.select:
select ta.id,ta.name,tu1.name as owner,ta.start_date,ta.end_date,ta.edit_time, ta.cost,ta.description,ta.create_time,tu2.name as create_by,tu3.name as edit_by from tbl_activity ta join tbl_user tu1 on ta.owner = tu1.id join tbl_user tu2 on ta.create_by = tu2.id left join tbl_user tu3 on ta.edit_by = tu3.id
and ta.name like '%' #{name} '%'
and tu1.name like '%' #{owner} '%'
and ta.start_date >= #{startDate}
and ta.end_date <= #{endDate}
order by create_time desc limit #{beginNo},#{pageSize}

select count(*) as totalRows from tbl_activity ta join tbl_user tu1 on ta.owner = tu1.id join tbl_user tu2 on ta.create_by = tu2.id left join tbl_user tu3 on ta.edit_by = tu3.id
and ta.name like '%' #{name} '%'
and tu1.name like '%' #{owner} '%'
and ta.start_date >= #{startDate}
and ta.end_date <= #{endDate}

mapper.delete:
delete from tbl_activity
where id in

    #{id}
